# Generate JSON
$json = @{
    heroes = $heroes | ForEach-Object {
        @{
            id = $_.id
            name = $_.name
            slug = $_.name.ToLower() -replace '\s+','-' -replace '[^a-z0-9-]',''
            role = $_.role
            image = ($_.name.ToLower() -replace '\s+','-' -replace '[^a-z0-9-]','') + ".png"
            durability = $_.dur
            offense = $_.off
            control = $_.ctrl
            difficulty = $_.diff
            early_game = $_.early
            mid_game = $_.mid
            late_game = $_.late
            specialties = $_.spec
            strong_against = $_.strong
            weak_against = $_.weak
            synergy_with = $_.synergy
            description = $_.desc
        }
    }
} | ConvertTo-Json -Depth 10

# Save to file
$outputPath = "c:\Users\sepirothx\Documents\3. Laravel Development\mlbb_tool\mlbb_management_tool\Modules\MLBBToolManagement\Data\heroes.json"
$json | Out-File -FilePath $outputPath -Encoding UTF8

Write-Host "Generated heroes.json with $($heroes.Count) heroes" -ForegroundColor Green
Write-Host "Saved to: $outputPath" -ForegroundColor Cyan
